html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}

<style>
    .table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: #B7CCF2;
    color:#000000;
    font-size:16px;
    }
    .print-format table, .print-format tr, 
    .print-format td, .print-format div, .print-format p {
        font-family: sans-serif;
        line-height: 90%;
        vertical-align: middle;
    }
    @media screen {
        .print-format {
            width: 210mm;
            padding: 10mm;
            min-height: 297mm;
            footer {page-break-after: always;}
        }
    }
</style>

<small>
{{ add_header(0,1,doc,letter_head, no_letterhead) }}
<table class="table table-condensed table-bordered">
  <tr>
    <td colspan="3">
        <b style="font-size:16px;">{{ doc.select_print_heading or (doc.print_heading if doc.print_heading != None
            else _(doc.doctype)) }}</b>  
    </td>
    <td style="width:34%">          
        <b style="font-size:16px; text-align:left; vertical-align:middle;">{{ _("# ") }}{{ doc.name }}</b><br>
    </td>
  </tr>
  <tr>
        <td>
            <b style="font-site:12px;"> {{ "Created By: "}} </b>
        </td>
        <td>
            {{ doc.created_by }}
        </td>
        <td>
            <b style="font-site:12px;"> {{ "Next Action By: "}} </b>
        </td>
        <td>
            {{ doc.next_action_by }}
        </td>
  </tr>
  {% if doc.allow_back_dated_entries == 1 %}
      <tr>
            <td>
                <b style="font-site:12px;"> {{ "Allow Back Dated Entries: "}} </b>
            </td>
            <td>
                {{ doc.allow_back_dated_entries }}
            </td>
            <td>
                <b style="font-site:12px;"> {{ "Back Dated Days: "}} </b>
            </td>
            <td>
                {{ doc.back_dated_days }}
            </td>
      </tr>
  {% endif %}
</table>

<table class="table table-condensed table-hover table-bordered">
        <tr>
            <th>Sr</th>
            <th>Contact Details</th>
            <th>Type of Comm</th>
            <th>Date Time</th>
            <th>Details</th>
        </tr>
        {%- for row in doc.call_details -%}
        <tr>
            <td style="width: 3%;">{{ row.idx }}</td>
            <td style="width: 20%;">
                {% if row.document == 'Lead' %}
                    {{ row.document_name }} : {{ row.lead_organisation_name }} <br>
                    {{ "Lead Status: " }} {{ row.lead_status }} <br>

                {% else %}
                    {{ row.document_name }} : {{ row.contact }} <br>
                {% endif %}
            </td>
            <td style="width: 10%;">{{ row.type_of_communication }}</td>
            <td style="width: 10%;">{{ row.communication_date }} <br>
                {{"Duration: "}} {{row.duration}} <br>
                {% if row.no_action_required == 0 %}
                    {{ "Next Action: "}} {{ row.next_action_date }}
                {% else %}
                    <b> {{ "No Action Needed" }} </b>
                {% endif %}
            </td>
            <td style="width: 57%;">{{ row.details }}
                {% if row.manager_notes %}
                    <br> <b> {{"Manager Rating: "}} </b> {{ row.manager_rating }} <br>
                    <b> {{"Manager Notes: "}} </b> {{ row.manager_notes }} 
                {% endif %}
            </td>
        </tr>
        {%- endfor -%}
    </tbody>
</table>
